Sound.Volume = 2
Sound.Parent = workspace

-- Anima√ß√£o
local tweenInfo = TweenInfo.new(1.5, Enum.EasingStyle.Quart, Enum.EasingDirection.Out)
local blurTween = TweenService:Create(Blur, tweenInfo, {Size = 24})
blurTween:Play()

Sound:Play()
local logoIn = TweenService:Create(Logo, tweenInfo, {ImageTransparency = 0})
logoIn:Play()

task.wait(3.5) -- Tempo da Intro

local logoOut = TweenService:Create(Logo, tweenInfo, {ImageTransparency = 1, Size = UDim2.new(0, 350, 0, 350)})
