local blurOut = TweenService:Create(Blur, tweenInfo, {Size = 0})

logoOut:Play()
bgOut:Play()
blurOut:Play()

task.wait(1.5)
IntroGui:Destroy()
Blur:Destroy()
Sound:Destroy()



